mapper.update:
UPDATE t_sentences
SET hot = hot + 1
WHERE id = #{i}

mapper.select:
SELECT TS.id, TS.content, TS.create_time, TS.from, TS.hot, TS.other1, TS.other2, TS.other3, GROUP_CONCAT(TT.name SEPARATOR ',') AS tags FROM t_sentences TS LEFT JOIN t_sentence_tag TST ON TS.id = TST.sentence_id LEFT JOIN t_tags TT ON TST.tag_id = TT.id WHERE TT.name IN
#{tag.name}
GROUP BY TS.id, TS.content, TS.create_time, TS.from, TS.hot, TS.other1, TS.other2, TS.other3

SELECT TS.id, TS.content, TS.`from`, TS.hot, TT.id AS tagId, TT.`from` AS tagFrom, GROUP_CONCAT(TT.name SEPARATOR ',') AS tags FROM t_sentences TS LEFT JOIN t_sentence_tag TST ON TS.id = TST.sentence_id LEFT JOIN t_tags TT ON TST.tag_id = TT.id WHERE TT.name IN
#{tag.name}
GROUP BY TS.id, TS.content, TS.`from`, TS.hot ORDER BY RAND() -- 随机排序 LIMIT 1; -- 只取一条数据
